import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ProfileService } from '../profile.service';

@Component({
  selector: 'app-edit-profile',
  templateUrl: './edit-profile.component.html',
  styleUrls: ['./edit-profile.component.scss']
})
export class EditProfileComponent {


  user:any
  profileForm: FormGroup;


  constructor(private profileservice:ProfileService, private router:Router,private formBuilder: FormBuilder,private activateRoute:ActivatedRoute, private authservice:Auths){

    let userID = this.activateRoute.snapshot.params['id'];

    this.profileForm = this.formBuilder.group({
      profile_img: '',
      username: '',
      firstname: '',
      lastname: '',
      email: '',
      mobile: '',
      dob: ['', Validators.required],
      gender: ['', Validators.required],
      city: ['', Validators.required],
      country: ['', Validators.required],
    })

    this.profileForm.patchValue({
      username: this.user.username, 
      firstname: this.user.firstname,
      lastname: this.user.lastname,
      email:this.user.email,
      mobile: this.user.mobile
    }); 
  }


  ngOnInit(){}


  fatchUserDetails(userId:any){
    this.authservice
  }


  createProfile(data:any){
    this.profileservice.createProfile(data).subscribe((res:any)=>{
      console.log(res)
    },
    (err:any)=>{
      console.log(err)
    })
  }

}
