import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.scss']
})
export class ProfileComponent {

  user:any;

  constructor(private router:Router, private activateRoute:ActivatedRoute, private profileservice:prof){
    let userID = this.activateRoute.snapshot.params['id'];
    this.fatchUserDetails(userID);
  }

  ngOnInit(){

  }



  fatchUserDetails(userId: any) {
    this.profileservice.getUserDetails(userId).subscribe((res:any)=>{
      this.user = res['user']
      this.userId = this.user['id']
    })
  }

}
