import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Route, Router } from '@angular/router';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.scss']
})
export class SignupComponent {

  signupForm = new FormGroup({
    firstname : new FormControl("", Validators.required),
    lastname : new FormControl("", Validators.required),
    email: new FormControl('', [Validators.required, Validators.email]),
    mobile: new FormControl('', [Validators.required, Validators.minLength(10),Validators.maxLength(10), Validators.pattern("^[0-9]*$")]),
    password: new FormControl('', [Validators.required, Validators.pattern("^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%^&*_=+-]).{8,12}$")])
  })
  submitted = false

  // mobileFormat = '(000)000-0000';
  // emailPattern = '^[a-z0-9._%+-]+@[a-z0-9.-]+\\.[a-z]{2,4}$';

  constructor(private router:Router, private authservice:AuthService){}

  signup(data:object){
    this.submitted = true
    console.log(data)
    this.authservice.doLogin(data).subscribe((res:any)=>{
      if(res) this.router.navigate(['profile']);
    })
  }

  login(){
    this.router.navigate(['login'])
  }

  togglePassword(event:any){
    console.log("called",event)
  }

}
