import { Component } from '@angular/core';
import {
  FormBuilder,
  FormControl,
  FormGroup,
  Validators,
} from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthService } from '../../auth/auth.service';
import { ProfileService } from '../profile.service';

@Component({
  selector: 'app-edit-profile',
  templateUrl: './edit-profile.component.html',
  styleUrls: ['./edit-profile.component.scss'],
})
export class EditProfileComponent {
  user: any;
  profileForm: FormGroup;

  constructor(
    private profileservice: ProfileService,
    private router: Router,
    private formBuilder: FormBuilder,
    private activateRoute: ActivatedRoute,
    private authservice: AuthService
  ) {
    let userID = this.activateRoute.snapshot.params['id'];
    this.fatchUserDetails(userID);
    this.profileForm = this.formBuilder.group({
      userId: ['',Validators.required],
      profile_img: '',
      username: '',
      firstname: '',
      lastname: '',
      email: '',
      mobile: '',
      bio:'',
      dob: ['', Validators.required],
      gender: ['', Validators.required],
      city: ['', Validators.required],
      country: ['', Validators.required],
    });

  }

  ngOnInit() {
    this.patchValue()
  }

  patchValue(){

    this.profileForm.patchValue({
      username: "test",
      firstname: "test",
      lastname: "test",
      email: "test",
      mobile: "test",
    });
  }

  fatchUserDetails(userId: any) {

    this.authservice.getUserDetails(userId).subscribe((res:any)=>{
      console.log(res)
      this.user = res['user']

      console.log("-this.user-",this.user['id'])
    })
  }

  createProfile(data: any) {
    data
    this.profileservice.createProfile(data).subscribe(
      (res: any) => {
        console.log(res);
      },
      (err: any) => {
        console.log(err);
      }
    );
  }
}
<app-header></app-header>
<div class="">
  <h3 class="bg-gray-400 font-extrabold px-60 text-2xl">
    Edit your profile
  </h3>
  <div class="flex items-center justify-center pt-10">
    <form [formGroup]="profileForm" (ngSubmit)="createProfile(profileForm?.value)">

      <label for="">Profile Photo : </label>
      <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="file" name="profile_img"
        formControlName="profile_img"><br>

      <label for="">username : </label>
      <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="username"
        formControlName="username" readonly><br>

      <label for="">firstname : </label>
      <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="firstname"
        formControlName="firstname" readonly><br>

      <label for="">lastname : </label>
      <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="lastname"
        formControlName="lastname" readonly><br>

      <label for="">email : </label>
      <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="email"
        formControlName="email" readonly><br>

      <label for="">mobile : </label>
      <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="mobile"
        formControlName="mobile" readonly><br>

      <label for="">gender : </label>
      <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="gender"
        formControlName="gender"><br>

      <label for="">DOB : </label>
      <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="date" name="dob"
        formControlName="dob"><br>

      <label for="">Bio : </label>
      <textarea class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="bio"
        formControlName="bio"></textarea><br>

      <label for="">city : </label>
      <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="city"
        formControlName="city"><br>

      <label for="">country : </label>
      <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="country"
        formControlName="country"><br>

      <button type="submit"
        class="mt-2 text-sm text-center bg-blue-600 text-white p-2 rounded font-medium">Save</button>
    </form>
  </div>


</div>
