import { Component } from '@angular/core';
import { FormGroup } from '@angular/forms';
import { ProfileService } from '../profile.service';

@Component({
  selector: 'app-edit-profile',
  templateUrl: './edit-profile.component.html',
  styleUrls: ['./edit-profile.component.scss']
})
export class EditProfileComponent {

  profileForm = new FormGroup({

  })

  constructor(private profileservice:ProfileService){}

  createProfile(data:any){
    this.profileservice.createProfile(data).subscribe((res:any)=>{

    },
    (err:any)=>{

    })
  }

}
<app-header></app-header>
<div class="">
    <h3 class="bg-gray-400 font-extrabold px-60 text-2xl">
        Edit your profile 
    </h3>
    <div class="flex items-center justify-center pt-10">
        <form class="">

            <label for="">Profile Photo : </label>
            <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="file" name="profileimage" formControllerName="profile_img"><br>

            <label for="">username : </label>
            <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="username" formControllerName="username" readonly><br>

            <label for="">firstname : </label>
            <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="firstname" formControllerName="firstname" readonly><br>

            <label for="">lastname : </label>
            <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="lastname" formControllerName="lastname" readonly><br>

            <label for="">email : </label>
            <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="email" formControllerName="email" readonly><br>

            <label for="">mobile : </label>
            <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="mobile" formControllerName="mobile" readonly><br>
            <label for="">mobile : </label>
            <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="gender" formControllerName="gender" readonly><br>
            <label for="">mobile : </label>
            <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="mobile" formControllerName="mobile" readonly><br>

            <label for="">Bio : </label>
            <textarea class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="bio" formControllerName="bio"></textarea><br>

            <label for="">city : </label>
            <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="city" formControllerName="city"><br>

            <label for="">country : </label>
            <input class="w-30 rounded border bg-gray-100 p-2 text-xs mb-1" type="text" name="country" formControllerName="country"><br>

            <button type="submit" class="mt-2 text-sm text-center bg-blue-600 text-white p-2 rounded font-medium">Save</button>
        </form>
    </div>


</div>
