import { Component } from '@angular/core';
import { environment } from 'src/environments/environment.development';
import { PostService } from '../post.service';

@Component({
  selector: 'app-add-comment',
  templateUrl: './add-comment.component.html',
  styleUrls: ['./add-comment.component.scss']
})
export class AddCommentComponent {

  post:any
  imageUrl = environment.apiURL

  constructor(private postservice:PostService, private activateRoute){}

  ngOnInit(){
    let postId = this.activateRoute.snapshot
    this.fatchPost(postId)
  }

  fatchPost(postId:any){
    this.postservice.getPostDetails(postId).subscribe((res:any)=>{
      this.post = res['post']
    })
  }


}
